import { Component, OnInit } from '@angular/core';
import { Booking } from '../booking.model';
import { NgForm } from '@angular/forms';
import { BookingService } from '../booking.service';
import { ToastrService } from 'ngx-toastr';
import { SubSink } from 'subsink';
import { Driver } from 'src/app/driver/driver.model';

@Component({
  selector: 'app-booking-form',
  templateUrl: './booking-form.component.html',
  styles: [
  ]
})
export class BookingFormComponent implements OnInit {
  driverList!: Driver[];
  subs = new SubSink();

  constructor(public service: BookingService, private toaster: ToastrService) {}

  ngOnInit(): void {
  }

  onSubmit(form: NgForm){
    if(this.service.formData.numBookingID == 0 || this.service.formData.numBookingID === undefined)
    this.insertRecord(form);
    else
      this.updateRecord(form);
  }

  insertRecord(form: NgForm) {
    this.service.PostBookingDetail().subscribe(
      res => {
        this.resetForm(form);
        this.toaster.success('Successfully Saved.', 'Booking Details');
        this.subs.sink = this.service.GetBookingDetails().subscribe(response => {
          this.service.list = response.bookings;
        });
      },
      err => { console.log(err); }
    )
  }

  updateRecord(form: NgForm) {
    this.service.PutBookingtDetail().subscribe(
      res => {
        this.resetForm(form);
        this.toaster.warning('Successfully Updated.', 'Booking Details');
        this.subs.sink = this.service.GetBookingDetails().subscribe(response => {
          this.service.list = response.bookings;
        });
      },
      err => {
        console.log(err);
      }
    )
  }

  resetForm(form: NgForm) {
    form.form.reset();
    this.service.formData = new Booking();
  }
}
<form novalidate autocomplete="off" #form="ngForm" (submit)="onSubmit(form)">
    <input type="hidden" name="numBookingID" [value]="service.formData.numBookingID" />
    <div class="form-row">
        <div class="form-group col-md-6">
            <label>From<sup class="text-danger">*</sup></label>
            <input class="form-control " placeholder="From..." name="varFromAddress"
                #varFromAddress="ngModel" [(ngModel)]="service.formData.varFromAddress"
                required [class.invalid]="varFromAddress.invalid &#038;&#038; varFromAddress.touched">
        </div>
        <div class="form-group col-md-6">
            <label>To<sup class="text-danger">*</sup></label>
            <input class="form-control " placeholder="To..." name="varToAddress"
            #varToAddress="ngModel" [(ngModel)]="service.formData.varToAddress"
            required [class.invalid]="varToAddress.invalid &#038;&#038; varToAddress.touched">
        </div>
    </div>
    <div class="form-row">
        <div class="form-group col-md-6">
            <label>Distance<sup class="text-danger">*</sup></label>
            <input class="form-control " placeholder="Distance..." name="numDistance"
                #numDistance="ngModel" [(ngModel)]="service.formData.numDistance"
                required [class.invalid]="numDistance.invalid &#038;&#038; numDistance.touched">
        </div>
        <div class="from-grop col-md-6">
            <!-- <label>SELECT COFFEE</label><br>
            <p-dropdown id="item" [options]="itemsList" [style]="{'width':'100%'}"
                optionLabel="varItemName" optionValue="numDriverID" name="item"
                [(ngModel)]="service.formData.numDriverID" placeholder="Select Coffee"
                [required]="true" (onChange)="CalculatePrice($event)">
            </p-dropdown> -->
        </div>
    </div>
    <div class="form-row">
        <div class="from-grop col-md-4">
            <!-- <label>SELECT COFFEE</label><br>
            <p-dropdown id="item" [options]="itemsList" [style]="{'width':'100%'}"
                optionLabel="varItemName" optionValue="numGoodsTypeID" name="item"
                [(ngModel)]="service.formData.numGoodsTypeID" placeholder="Select Coffee"
                [required]="true" (onChange)="CalculatePrice($event)">
            </p-dropdown> -->
        </div>
        <div class="form-group col-md-4">
            <label>Weight<sup class="text-danger">*</sup></label>
            <input class="form-control " placeholder="KG..." name="numWeight"
            #numWeight="ngModel" [(ngModel)]="service.formData.numWeight"
            required [class.invalid]="numWeight.invalid &#038;&#038; numWeight.touched">
        </div>
        <div class="form-group col-md-4">
            <label>Price<sup class="text-danger">*</sup></label>
            <input class="form-control " placeholder="numPrice..." name="numPrice"
                #numPrice="ngModel" [(ngModel)]="service.formData.numPrice"
                required [class.invalid]="numPrice.invalid &#038;&#038; numPrice.touched">
        </div>
        <div class="form-group col-md-4">
            <label>Weight<sup class="text-danger">*</sup></label>
            <input class="form-control " placeholder="KG..." name="numWeight"
            #numWeight="ngModel" [(ngModel)]="service.formData.numWeight"
            required [class.invalid]="numWeight.invalid &#038;&#038; numWeight.touched">
        </div>
    </div>



    <div class="form-group">
        <button class="btn btn-primary" type="submit" [disabled]="form.invalid">SUBMIT</button>
    </div>
</form>
